mapper.select:
select count(1)
from tbl_finance_details
where
user_id=#{userId}
and moneyfrom_type=#{moneyfromType}
and moneyfrom_description=#{moneyfromDescription}

mapper.update:
update
tbl_finance_details
set
reward_money=reward_money+#{rewardMoney},
bonus_money=bonus_money+#{bonusMoney}
where
user_id=#{userId}
and moneyfrom_type=#{moneyfromType}
and moneyfrom_description=#{moneyfromDescription}
